import React from 'react';

let websocket

export default class AsyncPageDefault extends React.Component {
  constructor(props) {
    super(props);
    this.state = this.start.bind(this)
    this.state = {
      connectionStatus: 'connecting...'
    };
  }

  start = () => {
    console.log('thissss');
    websocket.send('{"op":"unconfirmed_sub"}');
  }

  stop = (event) => {
    console.log('thissss',event);
    //websocket.send('{"op":"unconfirmed_unsub"}');
    this.setState({
      connectionStatus: 'disconnected'
    })
  }

  updateBtcArray = (btcData) => {
    let btcHash = btcData.x.hash;
    let out = btcData.x.out;
    let btcTotalAmount = 0;
    for(let i = 0; i < out.length; i++){
      btcTotalAmount += out[i].value;
    }
    console.log(btcTotalAmount);
  }

  componentDidMount = () => {
    websocket = new WebSocket('wss://ws.blockchain.info/inv');
    websocket.onopen = () => {
      console.log('jdjjdj');
      this.setState({
        connectionStatus: 'connected'
      })
      this.start()
    };

    websocket.onerror = () => {
      this.stop()
    };

    websocket.onmessage = (event) => {
      console.log('event---',JSON.parse(event.data));
      let btcData = event.data
      if (btcData.op == 'utx'){
        this.updateBtcArray(btcData)
      }
      websocket.close();
      this.stop(event)
    }
  }



  render() {
    const { connectionStatus } = this.state
    return (
      <div>
        {connectionStatus}
      </div>
    );
  }
}
