import React from 'react';
import TextField from '@material-ui/core/TextField';

export default class FindBtc extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      btcAmount: ''
    }
  }

  _handleBtcValue = (ev) => {
    const amountRegext = /^[+-]?\d+(\.\d+)?$/.test(ev.target.value)
    this.setState({
      btcAmount: ev.target.value
    })
  }
  render () {
    const { btcArray } = this.props
    const { btcAmount } = this.state
    return (
      <div>
        <h3>Find BTC by Value</h3>
        <TextField
          id="standard-search"
          label="Enter Amount"
          type="search"
          margin="normal"
          onChange={this._handleBtcValue}
          value={btcAmount}
        />
        <button
          type="button"
          onClick={this._findBtc}
        >
        </button>
      </div>
    )
  }
}
