import React from 'react';
import TextField from '@material-ui/core/TextField';

export default class FindBtc extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      btcAmount: ''
    }
  }

  handleBtcValue = (ev) => {
    const amountRegext = /^[+-]?\d+(\.\d+)?$/
    console.log('ev', ev.target.value);
    if(amountRegext.test(ev.target.value)) {
      this.setState({
        btcAmount: ev.target.value
      })
    }
  }
  render () {
    const { btcArray, btcAmount } = this.props
    return (
      <div>
        <h3>Find BTC by Value</h3>
        <TextField
          id="standard-search"
          label="Enter Amount"
          type="search"
          margin="normal"
          onChange={this.handleBtcValue}
          value={btcAmount}
        />
      </div>
    )
  }
}
