import React from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
  },
  paper: {
    marginTop: theme.spacing(3),
    width: '100%',
    overflowX: 'auto',
    marginBottom: theme.spacing(2),
  },
  table: {
    minWidth: 650,
  },
}));

export default class FindBtc extends React.Component {
  classes = useStyles();
  constructor(props) {
    super(props)
    this.state = {
      btcAmount: '',
      error: '',
      outputArray: []
    }
  }

  _handleBtcValue = (ev) => {

    this.setState({
      btcAmount: ev.target.value
    })
  }

  _findBtc = (ev) => {
    const { btcAmount } = this.state
    const amountRegex = /^[+-]?\d+(\.\d+)?$/.test(btcAmount)
    if(amountRegex) {
      this._getValue(btcAmount)
    } else {
      this.setState({
        error: 'Please Enter Valid Number. eg(12,1.2,0.12)'
      })
    }
  }

  _getValue = (btcAmount) => {
    const { btcArray } = this.props;
    let outputArray = []
    let newArray = btcArray.slice();
    let count = 0;
    while (count < 3) {
      let ouput = newArray.reduce((prev, curr, index) => Math.abs(curr.amount - btcAmount) < Math.abs(prev.amount - btcAmount) ? curr : prev);
      newArray = newArray.filter((item) => {
        if(item.id !== ouput.id) {
          return item
        }
      })
      outputArray.push(ouput)
      count++;
    }
    this.setState({
      outputArray
    })
    console.log('outputArray----',outputArray,'btcAmount---',btcAmount);
  }

  render () {
    const { btcArray } = this.props
    const { btcAmount, error, outputArray } = this.state
    return (
      <div>
        <h3>Find BTC by Value</h3>
        <TextField
          id="standard-search"
          label="Enter Amount"
          type="search"
          margin="normal"
          onChange={this._handleBtcValue}
          value={btcAmount}
        />
        <button
          type="button"
          onClick={this._findBtc}
        >
          Find BTC
        </button>
        <p>{error}</p>
        {outputArray.forEach((item, index) => {
          return(<ul>

          </ul>)
        })}
        <div className={classes.root}>
          <Paper className={classes.paper}>
            <Table className={classes.table} size="small">
              <TableHead>
                <TableRow>
                  <TableCell>HASH</TableCell>
                  <TableCell>AMOUNT</TableCell>
                  <TableCell>TIME STAMP</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {outputArray.map(btc => (
                  <TableRow key={btc.id}>
                    <TableCell>{btc.id}</TableCell>
                    <TableCell>{btc.amount}</TableCell>
                    <TableCell>{btc.timeStamp}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        </div>
      </div>
    )
  }
}
