import React from 'react';
import TextField from '@material-ui/core/TextField';

export default class FindBtc extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      btcAmount: '',
      error: '',
      searchResult: []
    }
  }

  _handleBtcValue = (ev) => {

    this.setState({
      btcAmount: ev.target.value
    })
  }

  _findBtc = (ev) => {
    const { btcAmount } = this.state
    const amountRegex = /^[+-]?\d+(\.\d+)?$/.test(btcAmount)
    if(amountRegex) {
      this._getValue(btcAmount)
    } else {
      this.setState({
        error: 'Please Enter Valid Number. eg(12,1.2,0.12)'
      })
    }
  }

  _getValue = (btcAmount) => {
    const { btcArray } = this.props;
    let outputArray = []
    let newArray = btcArray.slice()
    const ouput = btcArray.reduce((prev, curr, index) => Math.abs(curr.amount - btcAmount) < Math.abs(prev.amount - btcAmount) ? curr : prev);
    newArray = newArray.filer((item) => {
      if(item.id !== ouput.id) {
        return item
      }
    })
    outputArray.push(ouput)
  }

  render () {
    const { btcArray } = this.props
    const { btcAmount, error } = this.state
    return (
      <div>
        <h3>Find BTC by Value</h3>
        <TextField
          id="standard-search"
          label="Enter Amount"
          type="search"
          margin="normal"
          onChange={this._handleBtcValue}
          value={btcAmount}
        />
        <button
          type="button"
          onClick={this._findBtc}
        >
          Find BTC
        </button>
        <p>{error}</p>
      </div>
    )
  }
}
